import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.for-each.js";
import "core-js/modules/esnext.iterator.map.js";
import { ref, onMounted } from "vue";
import { getRanderingSorted, getPointSorted } from "@/api/rendering";
export default {
  name: "MapWindow",
  setup() {
    const map = ref(null);
    const polygons = ref([]);
    const markerList = ref([]);

    // 加载并初始化地图
    const loadAMapApi = () => {
      const script = document.createElement("script");
      script.type = "text/javascript";
      script.src = "https://webapi.amap.com/maps?v=2.0&key=a6042dfaa8137c1bd9f59034cd3021a3";
      document.head.appendChild(script);
      script.onload = () => {
        initMap();
      };
    };
    const initMap = () => {
      const layer = new AMap.createDefaultLayer({
        zooms: [3, 20],
        visible: true,
        opacity: 1,
        zIndex: 0
      });
      map.value = new AMap.Map("container", {
        viewMode: "2D",
        zoom: 16.3,
        center: [113.266949, 35.188365],
        layer: [layer]
      });
    };

    // 获取并渲染数据
    onMounted(async () => {
      try {
        const response = await getRanderingSorted();
        const response_tow = await getPointSorted();
        console.log("Response:", response.data);
        console.log("Point:", response_tow.data);
        if (response.data.code === 0 && Array.isArray(response.data.data) && response_tow.data.code === 0) {
          const formattedPolygons = response.data.data.map(item => {
            const coordinates = item.parking_area;
            console.log(coordinates);
            // 创建 AMap.Polygon 实例时，确保它没有被响应式包裹
            const polygon = new AMap.Polygon({
              path: coordinates,
              fillColor: "#ccebc5",
              strokeOpacity: 1,
              fillOpacity: 0.5,
              strokeColor: "#2b8cbe",
              strokeWeight: 1,
              strokeStyle: "dashed",
              strokeDasharray: [5, 5]
            });
            return polygon;
          });
          const formattedPoint = response_tow.data.data.map(item => {
            const coordinates_tow = item.coordinates;
            const id = item.id;
            console.log(coordinates_tow);
            console.log(id);
            const marker = new AMap.Marker({
              position: new AMap.LngLat(coordinates_tow),
              //经纬度对象，也可以是经纬度构成的一维数组[116.39, 39.9]
              title: id
            });
            return marker;
          });
          polygons.value = formattedPolygons; // 更新 polygons
          markerList.value = formattedPoint;

          // 延迟添加多边形到地图
          setTimeout(() => {
            addPolygonsToMap(polygons.value);
            addPolygonsToMap(markerList.value);
          }, 500);
        } else {
          console.error("Invalid data format", response.data);
        }
      } catch (error) {
        console.error("Error fetching polygons:", error);
      }
    });

    // 将多边形添加到地图
    const addPolygonsToMap = (polygons, markerList) => {
      if (Array.isArray(polygons)) {
        polygons.forEach(polygon => {
          // 确保每个 polygon 是 AMap.Polygon 实例
          if (polygon instanceof AMap.Polygon) {
            map.value.add(polygon); // 添加有效的 AMap.Polygon 实例到地图
          } else {
            console.error("Invalid polygon item", polygon);
          }
        });
      } else {
        console.error("Polygons is not an array:", polygons);
      }

      // 确保 markerList 是一个数组
      if (Array.isArray(markerList)) {
        markerList.forEach(marker => {
          // 确保每个 marker 是 AMap.Marker 实例
          if (marker instanceof AMap.Marker) {
            map.value.add(marker); // 添加有效的 AMap.Marker 实例到地图
          } else {
            console.error("Invalid marker item", marker);
          }
        });
      } else {
        console.error("markerList is not an array:", markerList);
      }
    };

    // 加载并初始化地图 API
    onMounted(() => {
      loadAMapApi();
    });
    return {
      map,
      polygons
    };
  }
};
