import axios from "axios";
import { defineComponent, reactive } from "vue";
import { Form, Input, Button, Card, Layout, InputPassword } from "ant-design-vue";
// import { reactive } from "vue";
export default defineComponent({
  components: {
    "a-layout": Layout,
    "a-layout-content": Layout.Content,
    "a-card": Card,
    "a-form": Form,
    "a-form-item": Form.Item,
    "a-input": Input,
    "a-input-password": InputPassword,
    "a-button": Button
  },
  data() {
    return {
      registerForm: reactive({
        username: "",
        password: "",
        confirm: "",
        employeeKey: "",
        phone: ""
      }),
      rules: {
        username: [{
          required: false,
          message: "请输入用户名",
          trigger: "blur"
        }],
        password: [{
          required: false,
          message: "请输入密码",
          pattern: /^\S{6,16}$/
        }],
        confirm: [{
          required: false,
          message: "请确认密码",
          trigger: "blur"
        }, {
          validator(_, value) {
            if (!value || value === this.registerForm.password) {
              return Promise.resolve();
            }
            return Promise.reject(new Error("两次输入的密码不一致"));
          }
        }],
        employeeKey: [{
          required: false
        }],
        phone: [{
          required: false,
          pattern: /^\S{5,9}$/,
          message: "请输入正确的手机号"
        }]
      }
    };
  },
  methods: {
    handleCancel() {
      this.$router.push("/");
      this.isModalVisible = false; // 关闭模态框
    },
    login() {
      const loginData = {
        username: this.registerForm.username,
        password: this.registerForm.password,
        employeeKey: this.registerForm.employeeKey,
        phone: this.registerForm.phone
      };
      console.log("Username:", this.form.username);
      console.log("Password:", this.form.password);
      console.log("EmployeeKey:", this.form.password);
      console.log("Phone:", this.form.password);
      axios.post("", loginData).then(response => {
        // 处理登录成功，例如保存token
        localStorage.setItem("token", response.data.token);
        // 跳转到主页或其他页面
        this.$router.push("/home");
      }).catch(error => {
        // 处理错误
        console.error("登录失败", error);
      });
    }
  }
});
