import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import "core-js/modules/esnext.iterator.for-each.js";
import "core-js/modules/esnext.iterator.map.js";
import { ref, onMounted } from "vue";
import { getRanderingSorted, getPointSorted } from "@/api/rendering";
export default {
  name: "MapWindow",
  setup() {
    const map = ref(null);
    const polygons = ref([]);
    const markerList = ref([]);

    // 加载并初始化地图
    const loadAMapApi = () => {
      const script = document.createElement("script");
      script.type = "text/javascript";
      script.src = "https://webapi.amap.com/maps?v=2.0&key=a6042dfaa8137c1bd9f59034cd3021a3";
      document.head.appendChild(script);
      script.onload = () => {
        initMap();
      };
    };

    // 在 initMap 中确保 map 被初始化
    const initMap = () => {
      const layer = new AMap.createDefaultLayer({
        zooms: [3, 20],
        visible: true,
        opacity: 1,
        zIndex: 0
      });
      map.value = new AMap.Map("container", {
        viewMode: "2D",
        zoom: 16.3,
        center: [113.266949, 35.188365],
        layer: [layer]
      });
      console.log("Map initialized:", map.value); // 确保 map 被正确初始化
    };

    // 获取并渲染数据
    onMounted(async () => {
      try {
        const response = await getRanderingSorted();
        const response_tow = await getPointSorted();
        console.log("Response:", response.data);
        console.log("Point:", response_tow.data);
        if (response.data.code === 0 && Array.isArray(response.data.data)) {
          const formattedPolygons = response.data.data.map(item => {
            const coordinates = item.parking_area; // 确保 parking_area 为有效数组
            if (coordinates && Array.isArray(coordinates) && coordinates.length > 0) {
              console.log("Valid coordinates:", coordinates);
              const polygon = new AMap.Polygon({
                path: coordinates,
                fillColor: "#ccebc5",
                strokeOpacity: 1,
                fillOpacity: 0.5,
                strokeColor: "#2b8cbe",
                strokeWeight: 1,
                strokeStyle: "dashed",
                strokeDasharray: [5, 5]
              });
              return polygon;
            } else {
              console.error("Invalid coordinates:", coordinates);
              return null;
            }
          }).filter(polygon => polygon !== null); // 过滤掉无效的多边形

          const formattedPoint = response_tow.data.data.map(item => {
            const coordinates_tow = item.coordinates;
            const id = item.id;
            console.log(coordinates_tow);
            console.log(id);

            // 检查坐标是否有效
            if (Array.isArray(coordinates_tow) && coordinates_tow.length === 1) {
              const [lng, lat] = coordinates_tow[0]; // 取第一个坐标对
              if (!isNaN(lng) && !isNaN(lat)) {
                const marker = new AMap.Marker({
                  position: new AMap.LngLat(lng, lat),
                  // 经度、纬度
                  title: id
                });
                return marker;
              } else {
                console.error(`Invalid coordinates for marker ${id}:`, coordinates_tow);
              }
            } else {
              console.error(`Invalid coordinates format for marker ${id}:`, coordinates_tow);
            }
            return null;
          }).filter(marker => marker !== null); // 过滤掉无效的 marker

          polygons.value = formattedPolygons; // 更新 polygons
          markerList.value = formattedPoint;

          // 延迟添加多边形到地图
          setTimeout(() => {
            addPolygonsToMap(polygons.value);
            addPolygonsToMap(markerList.value);
          }, 500);
        } else {
          console.error("Invalid data format", response.data);
        }
      } catch (error) {
        console.error("Error fetching polygons:", error);
      }
    });

    // 将多边形添加到地图
    // 延迟添加多边形到地图，确保地图已初始化
    const addPolygonsToMap = (polygons, markerList) => {
      if (!map.value) {
        console.error("Map is not initialized yet!");
        return;
      }

      // 确保 polygons 是数组，并且每个 polygon 都是 AMap.Polygon 实例
      if (Array.isArray(polygons)) {
        polygons.forEach(polygon => {
          if (polygon instanceof AMap.Polygon) {
            map.value.add(polygon); // 添加有效的 AMap.Polygon 实例到地图
          } else {
            console.error("Invalid polygon item", polygon);
          }
        });
      } else {
        console.error("Polygons is not an array:", polygons);
      }

      // 确保 markerList 是数组，并且每个 marker 都是 AMap.Marker 实例
      if (Array.isArray(markerList)) {
        markerList.forEach(marker => {
          if (marker instanceof AMap.Marker) {
            map.value.add(marker); // 添加有效的 AMap.Marker 实例到地图
          } else {
            console.error("Invalid marker item", marker);
          }
        });
      } else {
        console.error("markerList is not an array:", markerList);
      }
    };

    // 加载并初始化地图 API
    onMounted(() => {
      loadAMapApi();
    });
    return {
      map,
      polygons
    };
  }
};
