import { defineComponent } from "vue";
import { Form, Input, Button, Card, Layout } from "ant-design-vue";
import { useForm } from "@ant-design/vue/es/form";
export default defineComponent({
  components: {
    "a-layout": Layout,
    "a-layout-content": Layout.Content,
    "a-card": Card,
    "a-form": Form,
    "a-form-item": Form.Item,
    "a-input": Input,
    "a-input-password": Input.Password,
    "a-button": Button
  },
  setup() {
    const form = useForm();
    const rules = {
      username: [{
        required: true,
        message: "请输入新用户名",
        trigger: "blur"
      }],
      password: [{
        required: true,
        message: "请输入密码",
        trigger: "blur"
      }],
      confirm: [{
        required: true,
        message: "请确认密码",
        trigger: "blur"
      }, {
        validator(_, value) {
          if (!value || value === form.getFieldValue("password")) {
            return Promise.resolve();
          }
          return Promise.reject(new Error("两次输入的密码不一致"));
        }
      }],
      employeeKey: [{
        required: false
      }],
      phone: [{
        required: false,
        pattern: /^1[3-9]\d{9}$/,
        message: "请输入正确的手机号"
      }]
    };
    const handleSubmit = values => {
      console.log("Received values of form: ", values);
      // 这里可以添加表单提交逻辑
    };
    const handleCancel = () => {
      // 这里可以添加取消逻辑
      form.resetFields();
    };
    return {
      form,
      rules,
      handleSubmit,
      handleCancel
    };
  }
});
