import "core-js/modules/es.array.push.js";
import { reactive } from "vue";
import { useAuthStore } from "@/storage/userStorage";
import { useRouter } from "vue-router";
import { Form, Input, Button, Card, Layout, InputPassword, message } from "ant-design-vue";
export default {
  components: {
    "a-layout": Layout,
    "a-layout-content": Layout.Content,
    "a-card": Card,
    "a-form-item": Form.Item,
    "a-input": Input,
    "a-input-password": InputPassword,
    "a-button": Button
  },
  data() {
    return {
      registerForm: reactive({
        username: "",
        password: "",
        confirm: "",
        employeeKey: "",
        phone: ""
      }),
      rules: {
        username: [{
          required: false,
          message: "请输入正确的用户名",
          trigger: /^\S{4,10}$/
        }],
        password: [{
          required: false,
          message: "请输入正确的密码",
          pattern: /^\S{8,16}$/
        }],
        confirm: [{
          required: false,
          message: "请确认密码",
          trigger: "blur"
        }, {
          validator: (_, value) => {
            // 使用箭头函数来捕获外围作用域中的 this
            if (!value || value === this.registerForm.password) {
              return Promise.resolve();
            }
            return Promise.reject(new Error("两次输入的密码不一致"));
          }
        }],
        employeeKey: [{
          required: false
        }],
        phone: [{
          required: false,
          pattern: /^\S{11,11}$/,
          message: "请输入正确的手机号"
        }]
      }
    };
  },
  methods: {
    async logins() {
      await this.handleSubmit(this.registerForm);
    },
    async handleSubmit(values) {
      const authStore = useAuthStore();
      const router = useRouter();
      console.log("staff_username:", values.username);
      console.log("staff_password:", values.password);
      console.log("staff_phone:", values.phone);
      try {
        await authStore.register({
          staff_username: values.username,
          // 使用后端需要的字段名
          staff_password: values.password,
          staff_telephone: values.phone || null // 可选字段
        });
        message.success("注册成功");
        await router.push({
          path: ""
        }); // 注册成功后跳转到登录页面
      } catch (error) {
        message.error("注册失败");
      }
    },
    handleCancel() {
      this.$router.push("");
      this.isModalVisible = false; // 关闭模态框
    }
  }
};
