import "core-js/modules/es.array.push.js";
import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.for-each.js";
import "core-js/modules/esnext.iterator.map.js";
import { ref, onMounted } from "vue";
import { getRanderingSorted, getPointSorted } from "@/api/rendering";
export default {
  name: "MapWindow",
  setup() {
    const map = ref(null);
    const polygons = ref([]);
    const markers = ref([]);

    // 加载并初始化地图
    const loadAMapApi = () => {
      const script = document.createElement("script");
      script.type = "text/javascript";
      script.src = "https://webapi.amap.com/maps?v=2.0&key=a6042dfaa8137c1bd9f59034cd3021a3";
      document.head.appendChild(script);
      script.onload = () => {
        initMap();
      };
    };
    const initMap = () => {
      const layer = new AMap.createDefaultLayer({
        zooms: [3, 20],
        visible: true,
        opacity: 1,
        zIndex: 0
      });
      map.value = new AMap.Map("container", {
        viewMode: "2D",
        zoom: 16.3,
        center: [113.266949, 35.188365],
        // 初始地图中心
        layer: [layer]
      });
    };

    // 获取并渲染多边形数据
    const fetchPolygons = async () => {
      try {
        const response = await getRanderingSorted();
        console.log("Polygons Response:", response.data);
        if (response.data.code === 0 && Array.isArray(response.data.data)) {
          const formattedPolygons = response.data.data.map(item => {
            const coordinates = item.coordinates;
            console.log(coordinates);
            // 创建 AMap.Polygon 实例
            const polygon = new AMap.Polygon({
              path: coordinates,
              fillColor: "#00BFFF",
              strokeOpacity: 1,
              fillOpacity: 0.5,
              strokeColor: "#2b8cbe",
              strokeWeight: 1,
              strokeStyle: "dashed",
              strokeDasharray: [5, 5]
            });
            return polygon;
          });
          polygons.value = formattedPolygons; // 更新 polygons

          // 延迟添加多边形到地图
          setTimeout(() => {
            addPolygonsToMap(polygons.value);
          }, 500);
        } else {
          console.error("Invalid polygon data format", response.data);
        }
      } catch (error) {
        console.error("Error fetching polygons:", error);
      }
    };

    // 获取并渲染自行车点数据
    const fetchBicyclePoints = async () => {
      try {
        const response = await getPointSorted();
        console.log("Bicycles Response:", response.data);
        if (response.data.code === 0 && Array.isArray(response.data.data)) {
          const bicycles = response.data.data;

          // 延迟添加点到地图
          setTimeout(() => {
            addMarkersToMap(bicycles);
          }, 500);
        } else {
          console.error("Invalid bicycle data format", response.data);
        }
      } catch (error) {
        console.error("Error fetching bicycles:", error);
      }
    };

    // 将多边形添加到地图
    const addPolygonsToMap = polygons => {
      if (Array.isArray(polygons)) {
        polygons.forEach(polygon => {
          if (polygon instanceof AMap.Polygon) {
            map.value.add(polygon); // 添加有效的 AMap.Polygon 实例到地图
          } else {
            console.error("Invalid polygon item", polygon);
          }
        });
      } else {
        console.error("Polygons is not an array:", polygons);
      }
    };

    // 将自行车点添加到地图
    const addMarkersToMap = bicycles => {
      if (Array.isArray(bicycles)) {
        bicycles.forEach(bicycle => {
          const {
            id,
            coordinates
          } = bicycle;
          const marker = new AMap.Marker({
            position: new AMap.LngLat(coordinates[0], coordinates[1]),
            // 设置点的坐标
            title: `Bicycle ID: ${id}` // 设置点的标题为 ID
          });
          markers.value.push(marker); // 将每个点标记保存到 markers 数组
          map.value.add(marker); // 添加标记到地图
        });
      }
    };

    // 初始化时加载地图和数据
    onMounted(() => {
      loadAMapApi(); // 加载高德地图 API
      fetchPolygons(); // 获取并渲染多边形数据
      fetchBicyclePoints(); // 获取并渲染自行车点数据
    });
    return {
      map,
      polygons,
      markers
    };
  }
};
