import { createRouter, createWebHistory } from "vue-router";
import MapWindow from "@/components/MapWindow.vue";
import DataSheet from "@/components/Data_Sheet.vue"; // 假设组件名为 DataSheet
import DispatchWindow from "@/components/DispatchWindow.vue";
import EnrollWindow from "@/MainWindow/EnrollWindow.vue";
import MainWindow from "@/MainWindow/MainWindow.vue";
import UserWindow from "@/MainWindow/UserWindow.vue";
import { commentProps } from "ant-design-vue/lib/comment";

const routes = [
  {
    path: "", // 根路径不需要重复的 component 配置
    component: MainWindow, // 根路径的组件
    children: [
      { path: "", component: MapWindow }, // 子路由的路径应该是相对路径
      { path: "data", component: DataSheet }, // 确保组件名和文件名一致
      { path: "dispatch", component: DispatchWindow },
    ],
  },
  { path: "/enroll", component: EnrollWindow }, // 确保路径以斜杠开头
  {
    path: "/user",
    component: UserWindow,
    children: [
      { path: "", component: MapWindow },
      { path: "/data", component: DataSheet },
      { path: "/dispatch", component: DispatchWindow },
    ],
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
